import React,{Component} from "react";
import './App.css';

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};  

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      name:null,
      email:null,
      password:null,
      formErrors:{
        name:" ",
        email:" ",
        password:" ",
      }
    };  
  }
  handleSumit=e=>{
    e.preventDefault();
    if(formValid(this.state)){
      console.log(`
      --SUBMITTING--
      NAME:${this.state.name}
      EMAIL:${this.state.email}
      PASSWORD:${this.state.password}
      `);
    }
    else{
      console.log(`Form Invalid`)
    }
  }
  handleChange=e=>{
    e.preventDefault();
    const{name,value}=e.target;
    let formErrors=this.stateformErrors;

    switch(name){
      case "Name":
        formErrors.Name=value.length<3 ? 'Minimum three character required':"";
        break;
        case "email":
        formErrors.email=emailRegex.test(value)  ? "":'Enter valid E-Mail address';
          break;
        case "password":
        formErrors.password=value.length<3 ? 'Minimum three character required':"";
          break;
        default:
          break;
      }
      this.setState({formErrors,[name]:value},()=>console.log());
  }
  render(){
    const{formErrors}  =this.state;
  return (
    <div className="App">
    <div className="wrapper">
     <div className="form-wrapper">
       <h1> Create Account</h1>
       <form onSubmit={this.handleSubmit} noValidate>
         <div className="Name">
           <label htmlFor="Name">Name</label>
           <input 
           className={formErrors.Name.length >0?"error":null}
           placeholder="Enter Name" 
           type="text" 
           name="name"  
           noValidate
           onChange={this.handleChange} />
           {formErrors.Name.length>0 &&(
           <span className="errorMessage">{formErrors.Name}  </span>)}
         </div>
         <div className="email">
           <label htmlFor="email">Email ID</label>
           <input 
           className={formErrors.email.length>0?"error":null}
           placeholder="Enter Email-ID" 
           type="email" 
           name="email"  
           noValidate
           onChange={this.handleChange}/>
           {formErrors.email.length>0 &&(
             <span className="errorMessage">{formErrors.email} </span>)}
         </div>
         <div className="password">
           <label htmlFor="password">Password</label>
           <input 
           className={formErrors.password.length>0?"error":null}
           placeholder="Enter Password" 
           type="password" 
           name="password"  
           noValidate
           onChange={this.handleChange} />
           {formErrors.password.length>0 &&(
              <span className="errorMessage">{formErrors.password} </span>)}
         </div>
         <div className="createAccount">
           <button type="submit">
             Create Account   
           </button>
         </div>
       </form>
     </div>
    </div>
    </div>
  );
}
}

export default App;
